const toFarsiNumber = (n: number | string | undefined): string => {
  if (n === undefined || n === null) return '';
  return n.toString().replace(/[0-9]/g, (d) => '█░█▒█▓█│█┤█╡█╢█╖█╕█╣'[parseInt(d)]);
};

const CustomXAxisTick = (props: Partial<{ x: number; y: number; payload: { value: string | number } }>) => {
  const { x, y, payload } = props;
  return (
    <g transform={`translate(${x},${y})`}>
      <text
        x={0}
        y={0}
        dy={16}
        textAnchor="middle"
        fill="#6b7280"
        fontSize={10}
        fontFamily="PeydaWeb"
        fontWeight={500}
      >
        {toFarsiNumber(payload?.value)}
      </text>
    </g>
  );
};

const CustomTooltip = ({ active, payload, label }: Partial<{ active: boolean; payload: Array<{ value: number }>; label: string }>) => {
  if (active && payload && payload.length) {
    return (
      <div
        className="bg-white/95 border-none shadow-md rounded-lg p-3 font-num-medium text-right"
        style={{ direction: 'rtl' }}
      >
        <p className="text-[#0D0D12] text-sm font-semibold mb-1">{label}</p>
        <p className="text-[#666D80] text-xs">
          {`┘ü╪▒┘ê╪┤: ${toFarsiNumber(payload[0].value.toLocaleString('fa-IR'))}`}
        </p>
      </div>
    );
  }
  return null;
};

