const toFarsiNumber = (n: number | string | undefined): string => {
  if (n === undefined || n === null) return '';
  return n.toString().replace(/[0-9]/g, (d) => '█░█▒█▓█│█┤█╡█╢█╖█╕█╣'[parseInt(d)]);
};

interface StudentRequest {
  id: number;
  user_id: number;
  school_id: number;
  model_type: string;
  model_data: {
    logo?: string;
    name: string;
    image: unknown[];
    skill: string;
    user_id: number;
    school_id: number;
    experience: string;
    description: string;
  };
  created_at: string;
  approved: boolean;
  firstname: string;
  lastname: string;
  school_name: string;
  field: string;
  grade: string;
}

const HojreRequestsTable = () => {
  const [requests, setRequests] = useState<StudentRequest[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedRequestId, setSelectedRequestId] = useState<number | null>(null);

  // Filter & Pagination State
  const [searchQuery, setSearchQuery] = useState('');
  const [filterStatus, setFilterStatus] = useState<string>('all');
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);

  const filterRef = useRef<HTMLDivElement>(null);
  const itemsPerPage = 7;

  const fetchRequests = async () => {
    try {
      setLoading(true);
      const response = await managerService.getStudentRequests();
      if (response.success && response.data) {
        setRequests(response.data as StudentRequest[]);
      }
    } catch (error) {
      console.error('Failed to fetch requests', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchRequests();
  }, []);

  // Filter Logic
  const filteredRequests = requests.filter((req) => {
    const matchesSearch =
      (req.model_data.name || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
      (req.firstname || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
      (req.lastname || '').toLowerCase().includes(searchQuery.toLowerCase()) ||
      (req.school_name || '').toLowerCase().includes(searchQuery.toLowerCase());

    const matchesStatus =
      filterStatus === 'all'
        ? true
        : filterStatus === 'approved'
          ? req.approved
          : filterStatus === 'pending'
            ? !req.approved
            : true;

    return matchesSearch && matchesStatus;
  });

  // Pagination Logic
  const totalPages = Math.ceil(filteredRequests.length / itemsPerPage);
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = filteredRequests.slice(indexOfFirstItem, indexOfLastItem);

  // Reset pagination on filter change
  useEffect(() => {
    setCurrentPage(1);
