  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [showSuccessModal, setShowSuccessModal] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [deleteId, setDeleteId] = useState<string | number | null>(null);
  const [isDeleting, setIsDeleting] = useState(false);

  const fetchProducts = async () => {
    setIsLoading(true);
    const { success, data } = await studentProductService.getProducts();
    if (success && data) {
      setProductsList(data);
    }
    setIsLoading(false);
  };

  useEffect(() => {
    let cancelled = false;
    const loadData = async () => {
        // We can set loading true here if not already true, 
        // but it is true by default in useState.
        try {
            const { success, data } = await studentProductService.getProducts();
            if (!cancelled && success && data) {
                setProductsList(data);
            }
        } catch (err) {
            console.error(err);
        } finally {
            if (!cancelled) setIsLoading(false);
        }
    };
    loadData();
    return () => { cancelled = true; };
  }, []);

  // Triggered by Table
  const handleDeleteProduct = (id: number | string) => {
    setDeleteId(id);
    setShowDeleteModal(true);
  };

  // Actual API Call
  const handleConfirmDelete = async () => {
    if (!deleteId) return;

    setIsDeleting(true);
    const { success, message } = await studentProductService.deleteProduct(deleteId);
    setIsDeleting(false);
    setShowDeleteModal(false);

    if (success) {
      fetchProducts();
      setModalMessage(message || '┘à╪¡╪╡┘ê┘ä ╪¿╪º ┘à┘ê┘ü┘é█î╪¬ ╪¡╪░┘ü ╪┤╪»');
