export function ProductTable({ products, loading = false, onDelete }: ProductTableProps) {
  const router = useRouter();
  const [currentPage, setCurrentPage] = useState(1);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState<'all' | 'approved' | 'pending'>('all');
  const [sortOption, setSortOption] = useState<string>('newest');

  const itemsPerPage = 8;

  // Filter Logic
  const filteredProducts = products.filter((p) => {
    const matchesSearch =
      (p.name && p.name.includes(searchQuery)) || (p.code && p.code.includes(searchQuery));
    const matchesStatus =
      statusFilter === 'all' ? true : statusFilter === 'approved' ? p.approved : !p.approved;
    return matchesSearch && matchesStatus;
  });

  // Sorting Logic
  const filteredAndSortedProducts = [...filteredProducts].sort((a, b) => {
    switch (sortOption) {
      case 'price-asc':
        return parseFloat(a.price || '0') - parseFloat(b.price || '0');
      case 'price-desc':
        return parseFloat(b.price || '0') - parseFloat(a.price || '0');
      case 'sold-desc':
        return (b.soldCount || 0) - (a.soldCount || 0);
      case 'oldest':
        return a.id - b.id;
      case 'newest':
      default:
        return b.id - a.id;
    }
  });



  // Calculate total pages based on filtered products
  const totalPages = Math.ceil(filteredAndSortedProducts.length / itemsPerPage);

  // Slice for current page
  const currentProducts = filteredAndSortedProducts.slice(
    (currentPage - 1) * itemsPerPage,
    currentPage * itemsPerPage,
  );

